{
  "plan": [
    "Check staged changes in git.",
    "Commit with an appropriate message and description.",
    "."
  ],
  "rationale": "To safely commit any staged changes with a descriptive message following conventional commit format.",
  "actions": [
    {
      "tool": "run",
      "cmd": "git status --porcelain",
      "timeoutSec": 10
    },
    {
      "tool": "git",
      "subtool": "commit",
      "args": {
        "message": "feat: add new functionality\n\n- Implemented new feature as requested\n- Ensured code follows best practices"
      }
    }
  ],
  "message_markdown": "If no changes are staged, the commit will fail; please stage changes first."
}

{
  "plan": [
    "Commit the current changes with an appropriate message and description."
  ],
  "rationale": "The git status shows modified and new files that need to be committed to the repository.",
  "actions": [
    {
      "tool": "git",
      "subtool": "commit",
      "args": {
        "message": "feat: implement new commands and session management\n\n- Added memory and session commands\n- Introduced session summarizer flow\n- Updated core agent and UI components\n- Removed project_bundle.txt"
      }
    }
  ]
}
